// Валидация входных данных для Docling
const items = $input.all();
const results = [];

for (const item of items) {
  const body = item.json.body || item.json;

  // Проверка наличия файла или URL
  if (!body.file && !body.fileUrl && !item.binary) {
    throw new Error('File, fileUrl or binary data is required');
  }

  // Параметры обработки
  const enableImageDescription = body.enable_image_description !== false;
  const sendToRag = body.send_to_rag !== false;

  // Метаданные
  const metadata = body.metadata || {};
  metadata.uploaded_at = new Date().toISOString();
  metadata.workflow = 'n8n-docling-process';

  results.push({
    json: {
      enable_image_description: enableImageDescription,
      send_to_rag: sendToRag,
      metadata: JSON.stringify(metadata),
      filename: body.filename || 'document'
    },
    binary: item.binary || {}
  });
}

return results;

// Обработка результата от Docling Service
const items = $input.all();
const results = [];

for (const item of items) {
  const response = item.json;

  if (!response.success) {
    throw new Error(`Docling processing failed: ${response.error}`);
  }

  // Формирование ответа
  const result = {
    success: true,
    document_id: response.document_id,
    filename: response.filename,
    chunks_count: response.chunks_count,
    has_pictures: response.metadata?.pictures_count > 0,
    pictures_count: response.metadata?.pictures_count || 0,
    pages: response.metadata?.pages,
    format: response.metadata?.format,
    message: 'Document processed successfully'
  };

  // Если были отправлены в RAG, добавляем информацию
  if (response.rag_ingestion) {
    result.rag_status = {
      total_chunks: response.rag_ingestion.total_chunks,
      successful: response.rag_ingestion.successful,
      failed: response.rag_ingestion.failed
    };
  }

  // Добавляем preview первых чанков
  if (response.chunks && response.chunks.length > 0) {
    result.chunks_preview = response.chunks.slice(0, 2).map(chunk => ({
      content: chunk.content.substring(0, 200) + '...',
      chunk_index: chunk.metadata.chunk_index
    }));
  }

  results.push({ json: result });
}

return results;
